// App.js
import React, { useState } from 'react';

const App = () => {
  const [page, setPage] = useState('main');

  const handleClickEnter = () => {
    setPage('question');
  };

  const handleClickYes = () => {
    setPage('yes');
  };

  const handleClickNo = () => {
    setPage('no');
  };

  const renderContent = () => {
    const containerStyle = {
      textAlign: 'center',
      marginTop: '200px',
      fontFamily: 'Arial, sans-serif',
      background: `url('https://eskipaper.com/images/cool-light-pink-wallpaper-1.jpg') no-repeat center center fixed`,
      backgroundSize: 'cover',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
    };

    const titleStyle = {
      fontSize: '96px', // Doubled font size
      fontWeight: 'bold',
      color: '#000000', // Set font color to black
    };

    const textStyle = {
      fontSize: '64px', // Doubled font size
      color: '#000000', // Set font color to black
      marginTop: '40px', // Increased margin
    };

    const buttonStyle = {
      fontSize: '32px', // Doubled button font size
      padding: '20px', // Doubled button size
      margin: '10px', // Increased margin
    };

    if (page === 'main') {
      return (
        <div style={containerStyle}>
          <h1 style={titleStyle}>ETH LONDON HACKATHON</h1>
          <button style={buttonStyle} onClick={handleClickEnter}>
            ENTER
          </button>
        </div>
      );
    } else if (page === 'question') {
      return (
        <div style={containerStyle}>
          <h2 style={textStyle}>
            Hey STROMWISE, hope you are done with the Google Cloud Bounty.
          </h2>
          <button style={buttonStyle} onClick={handleClickYes}>
            Yes
          </button>
          <button style={buttonStyle} onClick={handleClickNo}>
            No
          </button>
        </div>
      );
    } else if (page === 'yes') {
      return (
        <div style={containerStyle}>
          <h2 style={textStyle}>Congratulations, you are eligible for winning Google Home.</h2>
        </div>
      );
    } else if (page === 'no') {
      return (
        <div style={containerStyle}>
          <h2 style={textStyle}>No problem! You can still win Google Home.</h2>
        </div>
      );
    }
  };

  return <div>{renderContent()}</div>;
};

export default App;
