// App.js
import React, { useState } from 'react';

const App = () => {
  const [page, setPage] = useState('main');

  const handleClickEnter = () => {
    setPage('question');
  };

  const handleClickYes = () => {
    setPage('yes');
  };

  const handleClickNo = () => {
    setPage('no');
  };

  const renderContent = () => {
    if (page === 'main') {
      return (
        <div style={{ textAlign: 'center', marginTop: '200px' }}>
          <h1>ETH LONDON HACKATHON 2023</h1>
          <button onClick={handleClickEnter}>ENTER</button>
        </div>
      );
    } else if (page === 'question') {
      return (
        <div style={{ textAlign: 'center', marginTop: '200px' }}>
          <h2>Hey STROMWISE, Hope you managed to build something for Google Cloud Bounty.</h2>
          <button style={{ marginRight: '20px' }} onClick={handleClickYes}>
            Yes
          </button>
          <button onClick={handleClickNo}>No</button>
        </div>
      );
    } else if (page === 'yes') {
      return (
        <div style={{ textAlign: 'center', marginTop: '200px' }}>
          <h2>Congratulations! Stromwise, you are eligible for winning Google Home.</h2>
        </div>
      );
    } else if (page === 'no') {
      return (
        <div style={{ textAlign: 'center', marginTop: '200px' }}>
          <h2>Don't be sad! You can still win Google Home.</h2>
        </div>
      );
    }
  };

  return <div>{renderContent()}</div>;
};

export default App;
