  background: `url('https://eskipaper.com/images/cool-light-pink-wallpaper-1.jpg') no-repeat center center fixed`,
  backgroundSize: 'cover',
  height: '100vh',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
};

const titleStyle = {
  fontSize: '96px',
  fontWeight: 'bold',
  color: '#000000',
};

const textStyle = {
  fontSize: '64px',
  color: '#000000',
  marginTop: '40px',
};

const buttonStyle = {
  fontSize: '32px',
  padding: '20px',
  margin: '10px',
};

const App = () => {
  const [page, setPage] = useState('main');

  const handleClickEnter = () => {
    setPage('question');
  };

  const handleClickYes = () => {
    setPage('yes');
  };

  const handleClickNo = () => {
    setPage('no');
  };

  // Your existing styles and code remain the same...

  if (page === 'main') {
    // ... (unchanged)
  } else if (page === 'question') {
    // ... (unchanged)
  } else if (page === 'yes') {
    return (
      <div style={containerStyle}>
        <h2 style={textStyle}>Congratulations, you are eligible for winning Google Home.</h2>
        <p style={textStyle}>Please check your email for further instructions.</p>
        <form style={{ marginTop: '20px' }}>
          <input type="text" placeholder="Enter your email" style={{ padding: '10px', fontSize: '24px' }} />
          <button style={buttonStyle}>Submit</button>
        </form>
        <div style={{ marginTop: '50px' }}>
          <p style={{ color: '#000000', fontSize: '20px' }}>For more information, visit our website.</p>
