        <div style={{ marginTop: '20px' }}>
          <p style={textStyle}>Explore more opportunities with our other partners!</p>
        </div>
        <footer style={{ position: 'fixed', bottom: 0, width: '100%', backgroundColor: '#f9f9f9', padding: '10px 0', textAlign: 'center' }}>
          <p style={{ fontSize: '14px', color: '#666' }}>Â© 2023 ETH London Hackathon</p>
        </footer>
      </div>
    );
  }
};

export default App;
