// App.js
import React, { useState } from 'react';

const App = () => {
  const [page, setPage] = useState('main');
  const [email, setEmail] = useState(''); // Declaring the 'email' state
  const [submitted, setSubmitted] = useState(false); // New state to track form submission

  const handleClickEnter = () => {
    setPage('question');
  };

  const handleClickYes = () => {
    setPage('yes');
  };

  const handleClickNo = () => {
    setPage('no');
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Perform actions with the email here
    console.log('Submitted email:', email);
  };

  const containerStyle = {
    textAlign: 'center',
    marginTop: '200px',
    fontFamily: 'Arial, sans-serif',
    background: `url('https://eskipaper.com/images/cool-light-pink-wallpaper-1.jpg') no-repeat center center fixed`,
    backgroundSize: 'cover',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
  };

  const titleStyle = {
    fontSize: '96px',
    fontWeight: 'bold',
    color: '#000000',
  };

  const textStyle = {
    fontSize: '64px',
    color: '#000000',
    marginTop: '40px',
  };

  const buttonStyle = {
    fontSize: '32px',
    padding: '20px',
    margin: '10px',
  };

  if (page === 'main') {
    return (
      <div style={containerStyle}>
        <h1 style={titleStyle}>ETH LONDON HACKATHON 2023</h1>
        <button style={buttonStyle} onClick={handleClickEnter}>
          ENTER
        </button>
      </div>
    );
  } else if (page === 'question') {
    return (
      <div style={containerStyle}>
        <h2 style={textStyle}>
          Hey STROMWISE, hope you are done with the Google Cloud Bounty.
        </h2>
        <button style={buttonStyle} onClick={handleClickYes}>
          Yes
        </button>
        <button style={buttonStyle} onClick={handleClickNo}>
          No
        </button>
      </div>
    );
  } else if (page === 'yes') {
    return (
      <div style={containerStyle}>
        <h2 style={textStyle}>Congratulations, you are eligible for winning Google Home.</h2>
        <p style={textStyle}>Please enter your email to receive further instructions:</p>
        <form onSubmit={handleFormSubmit} style={{ marginTop: '20px' }}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={handleEmailChange}
            style={{ padding: '10px', fontSize: '24px' }}
          />
          <button style={buttonStyle}>Submit</button>
        </form>
        {submitted && <p style={textStyle}>Thank You</p>} {/* Render "Thank You" when submitted is true */}
      </div>
    );
  } else if (page === 'no') {
    return (
      <div style={containerStyle}>
        <h2 style={textStyle}>No problem! You can still win Google Home.</h2>
        <img
          src="https://www.freepngimg.com/thumb/web_design/31835-7-coder-file.png" // Add your image URL here
          alt="Just code something Better"
          style={{ width: '300px', height: '300px', marginTop: '20px' }} // Adjust styling as needed
        />
      </div>
    );
  }
};

export default App;
